import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import AdminForm from "./AdminForm";

// Mock service data (For illustration purposes)
const mockServices = [
    {
        id: 1,
        name: "Pension Form",
        description: "Help with pension form submissions",
        document1: "Pension ID",
        document2: "Bank Passbook",
        document3: "Aadhar Card",
    },
    {
        id: 2,
        name: "Insurance Form",
        description: "Fill out your insurance claim",
        document1: "Insurance Policy",
        document2: "Claim Form",
        document3: "Aadhar Card",
    },
];

function Services() {
    const [services, setServices] = useState(mockServices);
    const [isAdmin, setIsAdmin] = useState(false); // Check if admin is logged in
    const navigate = useNavigate();

    // Check if the admin is logged in
    useEffect(() => {
        const isLoggedIn = localStorage.getItem("isAdminLoggedIn");
        if (isLoggedIn) {
            setIsAdmin(true);
        }
    }, []);

    // Add new service
    const addService = (service) => {
        setServices((prevServices) => [...prevServices, service]);
    };

    // Edit existing service
    const editService = (updatedService) => {
        setServices((prevServices) =>
            prevServices.map((service) =>
                service.id === updatedService.id ? updatedService : service
            )
        );
    };

    // Delete service
    const deleteService = (id) => {
        setServices((prevServices) =>
            prevServices.filter((service) => service.id !== id)
        );
    };

    return (
        <div className="p-8">
            {/* Admin Adding Service Button */}
            {isAdmin && (
                <div className="flex justify-between items-center">
                    <h1 className="text-3xl font-semibold">Services</h1>
                    <button
                        onClick={() => navigate("/add-service")}
                        className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition"
                    >
                        + Add Service
                    </button>
                </div>
            )}

            {/* Display Services */}
            <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {services.map((service) => (
                    <div
                        key={service.id}
                        className="border p-4 rounded-lg shadow-md hover:shadow-lg transition cursor-pointer"
                        onClick={() =>
                            !isAdmin && navigate(`/service/${service.id}`)
                        }
                    >
                        <h2 className="text-xl font-bold">{service.name}</h2>
                        <p className="text-gray-700">{service.description}</p>

                        {/* Admin Options (Edit & Delete) */}
                        {isAdmin && (
                            <div className="mt-4 flex justify-between">
                                <button
                                    onClick={() => navigate(`/edit-service/${service.id}`)}
                                    className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-600"
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => deleteService(service.id)}
                                    className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600"
                                >
                                    Delete
                                </button>
                            </div>
                        )}
                    </div>
                ))}
            </div>

            {/* Pass addService function to AdminForm if adding service */}
            <AdminForm addService={addService} />
        </div>
    );
}

export default Services;
