import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

// Mock service data (for illustration)
const mockServices = [
    {
        id: 1,
        name: "Pension Form",
        description: "Help with pension form submissions",
        document1: "Pension ID",
        document2: "Bank Passbook",
        document3: "Aadhar Card",
    },
    {
        id: 2,
        name: "Insurance Form",
        description: "Fill out your insurance claim",
        document1: "Insurance Policy",
        document2: "Claim Form",
        document3: "Aadhar Card",
    },
];

function ClientForm() {
    const { serviceId } = useParams();
    const [service, setService] = useState(null);
    const [formData, setFormData] = useState({
        fullName: "",
        mobile: "",
        email: "",
        documents: [],
    });

    const navigate = useNavigate();

    // Find the service based on the serviceId
    useEffect(() => {
        const selectedService = mockServices.find(
            (service) => service.id === parseInt(serviceId)
        );
        setService(selectedService);
    }, [serviceId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleFileChange = (e) => {
        setFormData((prevData) => ({
            ...prevData,
            documents: [...prevData.documents, ...e.target.files],
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Client form submitted with data:", formData);

        // Navigate to a confirmation or success page
        navigate("/services");
    };

    if (!service) {
        return <div>Loading...</div>;
    }

    return (
        <div className="p-8">
            <h2 className="text-2xl font-semibold mb-4">{service.name} - Apply</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-4">
                    <label htmlFor="fullName" className="block text-sm font-semibold">Full Name</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        className="border p-2 w-full rounded-md"
                        required
                    />
                </div>

                <div className="mb-4">
                    <label htmlFor="mobile" className="block text-sm font-semibold">Mobile Number</label>
                    <input
                        type="text"
                        name="mobile"
                        value={formData.mobile}
                        onChange={handleChange}
                        className="border p-2 w-full rounded-md"
                        required
                    />
                </div>

                <div className="mb-4">
                    <label htmlFor="email" className="block text-sm font-semibold">Email Address</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="border p-2 w-full rounded-md"
                        required
                    />
                </div>

                <div className="mb-4">
                    <label htmlFor="documents" className="block text-sm font-semibold">Attach Documents</label>
                    <input
                        type="file"
                        name="documents"
                        onChange={handleFileChange}
                        className="border p-2 w-full rounded-md"
                        multiple
                        required
                    />
                </div>

                <button
                    type="submit"
                    className="bg-green-500 text-white px-6 py-2 rounded-full"
                >
                    Submit
                </button>
            </form>
        </div>
    );
}

export default ClientForm;
