import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { fetchServices, deleteService } from "./../api";

function Services() {
  const [services, setServices] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const isLoggedIn = localStorage.getItem("isAdminLoggedIn");
    if (isLoggedIn) {
      setIsAdmin(true);
    }

    fetchServices().then(setServices); // Fetch services from the backend
  }, []);

  const handleDelete = async (id) => {
    await deleteService(id);
    setServices((prev) => prev.filter((service) => service._id !== id));
  };

  return (
    <div className="p-8">
      {isAdmin && (
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-semibold">Services</h1>
          <button
            onClick={() => navigate("/add-service")}
            className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition"
          >
            + Add Service
          </button>
        </div>
      )}

      <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service) => (
          <div
            key={service._id}
            className="border p-4 rounded-lg shadow-md hover:shadow-lg transition cursor-pointer"
          >
            <h2 className="text-xl font-bold">{service.name}</h2>
            <p className="text-gray-700">{service.description}</p>

            {isAdmin && (
              <div className="mt-4 flex justify-between">
                <button
                  onClick={() => navigate(`/edit-service/${service._id}`)}
                  className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-600"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(service._id)}
                  className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Services;
