import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Services() {
  const [services, setServices] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const isLoggedIn = localStorage.getItem("isAdminLoggedIn");
    setIsAdmin(!!isLoggedIn);

    // Fetch services from the backend
    axios.get("/api/services")
      .then((response) => setServices(response.data))
      .catch((error) => console.error("Error fetching services:", error));
  }, []);

  return (
    <div className="p-8">
      <h1 className="text-3xl font-semibold">Services</h1>

      <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service) => (
          <div
            key={service._id}
            className="border p-4 rounded-lg shadow-md hover:shadow-lg transition cursor-pointer"
            onClick={() =>
              isAdmin
                ? navigate(`/edit-service/${service._id}`)
                : navigate(`/client-form/${service._id}`)
            }
          >
            <h2 className="text-xl font-bold">{service.name}</h2>
            <p className="text-gray-700">{service.description}</p>
            <p className="text-gray-700 font-semibold">Cost: â‚¹{service.cost}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Services;
