import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function AdminForm({ addService }) {
  const [serviceName, setServiceName] = useState("");
  const [serviceDescription, setServiceDescription] = useState("");
  const [document1, setDocument1] = useState("");
  const [document2, setDocument2] = useState("");
  const [document3, setDocument3] = useState("");
  const [image, setImage] = useState(null);

  const navigate = useNavigate();

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Create new service object
    const newService = {
      id: Date.now(), // generate a unique ID based on timestamp
      name: serviceName,
      description: serviceDescription,
      document1,
      document2,
      document3,
      image: image || "https://mamatafertility.com/wp-content/themes/consultix/images/no-image-found-360x250.png", // default image if none uploaded
    };

    // Call addService function passed from Services.js
    addService(newService);

    // Navigate back to the services page after adding the service
    navigate("/");
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-semibold">Add Service</h1>

      <form onSubmit={handleSubmit} className="mt-6 space-y-4">
        <div>
          <label htmlFor="serviceName" className="block text-sm font-medium text-gray-700">Service Name</label>
          <input
            type="text"
            id="serviceName"
            value={serviceName}
            onChange={(e) => setServiceName(e.target.value)}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="serviceDescription" className="block text-sm font-medium text-gray-700">Service Description</label>
          <textarea
            id="serviceDescription"
            value={serviceDescription}
            onChange={(e) => setServiceDescription(e.target.value)}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="document1" className="block text-sm font-medium text-gray-700">Document 1</label>
          <input
            type="text"
            id="document1"
            value={document1}
            onChange={(e) => setDocument1(e.target.value)}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="document2" className="block text-sm font-medium text-gray-700">Document 2</label>
          <input
            type="text"
            id="document2"
            value={document2}
            onChange={(e) => setDocument2(e.target.value)}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="document3" className="block text-sm font-medium text-gray-700">Document 3</label>
          <input
            type="text"
            id="document3"
            value={document3}
            onChange={(e) => setDocument3(e.target.value)}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="image" className="block text-sm font-medium text-gray-700">Upload Image</label>
          <input
            type="file"
            id="image"
            onChange={(e) => setImage(URL.createObjectURL(e.target.files[0]))}
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <button
          type="submit"
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Add Service
        </button>
      </form>
    </div>
  );
}

export default AdminForm;
