import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function ClientForm() {
  const { serviceId } = useParams();
  const [service, setService] = useState(null);
  const [formData, setFormData] = useState({
    fullName: "",
    mobile: "",
    email: "",
    documents: [],
  });

  // Fetch the service details
  useEffect(() => {
    axios
      .get(`/api/services/${serviceId}`)
      .then((response) => setService(response.data))
      .catch((error) => console.error("Error fetching service:", error));
  }, [serviceId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleFileChange = (index, file) => {
    const updatedDocuments = [...formData.documents];
    updatedDocuments[index] = file;
    setFormData((prevData) => ({ ...prevData, documents: updatedDocuments }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formPayload = new FormData();

    formPayload.append("fullName", formData.fullName);
    formPayload.append("mobile", formData.mobile);
    formPayload.append("email", formData.email);

    formData.documents.forEach((file, index) => {
      if (file) formPayload.append(`document${index + 1}`, file);
    });

    axios
      .post(`/api/clients/submit`, formPayload)
      .then((response) => alert("Form submitted successfully"))
      .catch((error) => console.error("Error submitting form:", error));
  };

  if (!service) return <p>Loading...</p>;

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold">{service.name}</h1>
      <form onSubmit={handleSubmit} className="mt-6 space-y-4">
        <div>
          <label htmlFor="fullName" className="block text-sm font-medium text-gray-700">
            Full Name
          </label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="mobile" className="block text-sm font-medium text-gray-700">
            Mobile Number
          </label>
          <input
            type="tel"
            id="mobile"
            name="mobile"
            value={formData.mobile}
            onChange={handleInputChange}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">
            Email
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
            className="mt-1 p-2 w-full border rounded-md"
          />
        </div>

        {service.documents.map((doc, index) => (
          <div key={index}>
            <label className="block text-sm font-medium text-gray-700">
              {doc}
            </label>
            <input
              type="file"
              onChange={(e) => handleFileChange(index, e.target.files[0])}
              className="mt-1 p-2 w-full border rounded-md"
            />
          </div>
        ))}

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default ClientForm;
