import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// Mock service data (For illustration purposes)
const mockServices = [
    {
        id: 1,
        name: "Pension Form",
        description: "Help with pension form submissions",
        document1: "Pension ID",
        document2: "Bank Passbook",
        document3: "Aadhar Card",
    },
    {
        id: 2,
        name: "Insurance Form",
        description: "Fill out your insurance claim",
        document1: "Insurance Policy",
        document2: "Claim Form",
        document3: "Aadhar Card",
    },
];

function Services() {
    const [services, setServices] = useState([]);
    const [isAdmin, setIsAdmin] = useState(false); // Check if admin is logged in
    const navigate = useNavigate();

    // Check if the admin is logged in
    useEffect(() => {
        const isLoggedIn = localStorage.getItem("isAdminLoggedIn");
        if (isLoggedIn) {
            setIsAdmin(true);
        }
        setServices(mockServices); // Set services data
    }, []);

    // Handle navigation for adding service or viewing service
    const handleServiceClick = (serviceId) => {
        if (isAdmin) {
            navigate(`/admin/form`);
        } else {
            navigate(`/client/form/${serviceId}`);
        }
    };

    return (
        <div className="p-8">
            {/* Admin Adding Service */}
            {isAdmin && (
                <div className="flex justify-between items-center">
                    <h1 className="text-3xl font-semibold">Services</h1>
                    <button
                        onClick={() => navigate("/admin/form")}
                        className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition"
                    >
                        + Add Service
                    </button>
                </div>
            )}

            {/* Display Services */}
            <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {services.map((service) => (
                    <div
                        key={service.id}
                        className="border p-4 rounded-lg shadow-md hover:shadow-lg transition cursor-pointer"
                        onClick={() => handleServiceClick(service.id)}
                    >
                        <h2 className="text-xl font-bold">{service.name}</h2>
                        <p className="text-gray-700">{service.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Services;
